import {getEmployeeList, enableOrDisableEmployee} from '@/api/employee'

export default  {
  //模型数据
  data() {
    return {
      name: '', //员工姓名，对应上面的输入框
      page: 1,
      pageSize: 10,
      total: 0,
      records: []
    }
  },

  created() {
    this.pageQuery()
  },

  methods: {
    //分页查询
    pageQuery() {
      //准备请求的参数
      const params = {name: this.name, page: this.page, pageSize: this.pageSize}

      //发送ajax请求，访问后端服务，获取分页数据
      getEmployeeList(params).then(res => {
        if (res.data.code == 1) {
          this.total = res.data.data.total
          this.records = res.data.data.records
        }
      }).catch(err => {
        this.$message.error('请求出错：' + err.message)
      })
    },
    //pageSize发生变化时触发
    handleSizeChange(pageSize) {
      this.pageSize = pageSize
      this.pageQuery()
    },
    //page发生变化时触发
    handleCurrentChange(page) {
      this.page = page
      this.pageQuery()
    },
    //启用禁用员工账号
    handleStartOrStop(row) {
      if (row.username === 'admin') {
        this.$message.error('admin为系统管理员账号，不能更改账号状态')
        return 
      }
      // alert(`id = ${row.id} status=${row.status}`)
      //弹出确认提示框
      this.$confirm('确认要修改当前员工账号的状态吗？','提示', 
      {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning',
      }).then(() => {
        const p = {
          id: row.id,
          status: !row.status ? 1 : 0
        }

        enableOrDisableEmployee(p).then(res => {
          if(res.data.code === 1) {
            this.$message.success('员工账号状态修改成功')
            this.pageQuery()
          }
        })
      })
    },
  }  
}
